import React, { useState, useEffect } from 'react';
import { Plus, ShoppingCart, X } from 'lucide-react';
import { checkout, getProducts, addCharges } from '../../services/api';
import PaymentModal from './PaymentModal';

// Modal de Punto de Venta
const POSModal = ({ booking, onClose, onAdded }) => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const data = await getProducts({ is_active: true });
      setProducts(data);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredProducts = products.filter(p =>
    p.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const addToCart = (product) => {
    const existing = cart.find(item => item.product_id === product.id);
    if (existing) {
      setCart(cart.map(item =>
        item.product_id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, {
        product_id: product.id,
        description: product.name,
        quantity: 1,
        unit_price: parseFloat(product.price)
      }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.product_id !== productId));
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
    } else {
      setCart(cart.map(item =>
        item.product_id === productId
          ? { ...item, quantity: newQuantity }
          : item
      ));
    }
  };

  const getTotal = () => {
    return cart.reduce((sum, item) => sum + (item.unit_price * item.quantity), 0);
  };

  const handleSubmit = async () => {
    if (cart.length === 0) {
      alert('Agregue al menos un producto');
      return;
    }

    setLoading(true);
    try {
      await addCharges(booking.id, cart);
      alert('Productos añadidos exitosamente');
      onAdded();
    } catch (error) {
      alert(error.response?.data?.error || 'Error al añadir productos');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 overflow-y-auto p-4">
      <div className="bg-white rounded-lg w-full max-w-4xl my-8">
        <div className="p-6 border-b flex justify-between items-center">
          <div>
            <h3 className="text-xl font-bold">Punto de Venta</h3>
            <p className="text-sm text-gray-600">
              {booking.full_name} - Habitación {booking.room_number}
            </p>
          </div>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="p-6 grid grid-cols-1 md:grid-cols-2 gap-6 max-h-[70vh] overflow-y-auto">
          {/* Lista de productos */}
          <div>
            <div className="mb-4">
              <input
                type="text"
                className="input"
                placeholder="Buscar productos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <div className="space-y-2 max-h-96 overflow-y-auto">
              {filteredProducts.map((product) => (
                <div
                  key={product.id}
                  onClick={() => addToCart(product)}
                  className="p-3 border rounded-lg hover:bg-blue-50 cursor-pointer transition-colors"
                >
                  <div className="flex justify-between items-start">
                    <div>
                      <p className="font-semibold">{product.name}</p>
                      {product.description && (
                        <p className="text-xs text-gray-500">{product.description}</p>
                      )}
                      <p className="text-xs text-gray-400 capitalize mt-1">{product.category}</p>
                    </div>
                    <p className="font-bold text-green-600">
                      Bs. {parseFloat(product.price).toFixed(2)}
                    </p>
                  </div>
                </div>
              ))}

              {filteredProducts.length === 0 && (
                <p className="text-center text-gray-500 py-8">No se encontraron productos</p>
              )}
            </div>
          </div>

          {/* Carrito */}
          <div>
            <h4 className="font-semibold mb-4">Carrito ({cart.length})</h4>

            {cart.length === 0 ? (
              <div className="text-center text-gray-500 py-12">
                <ShoppingCart className="w-16 h-16 mx-auto mb-4 opacity-30" />
                <p>Carrito vacío</p>
                <p className="text-sm">Seleccione productos de la izquierda</p>
              </div>
            ) : (
              <>
                <div className="space-y-2 mb-4 max-h-64 overflow-y-auto">
                  {cart.map((item) => (
                    <div key={item.product_id} className="p-3 border rounded-lg">
                      <div className="flex justify-between items-start mb-2">
                        <p className="font-semibold text-sm">{item.description}</p>
                        <button
                          onClick={() => removeFromCart(item.product_id)}
                          className="text-red-500 hover:text-red-700"
                        >
                          <X className="w-4 h-4" />
                        </button>
                      </div>
                      <div className="flex justify-between items-center">
                        <div className="flex items-center gap-2">
                          <button
                            onClick={() => updateQuantity(item.product_id, item.quantity - 1)}
                            className="w-6 h-6 bg-gray-200 rounded hover:bg-gray-300"
                          >
                            -
                          </button>
                          <span className="w-8 text-center font-semibold">{item.quantity}</span>
                          <button
                            onClick={() => updateQuantity(item.product_id, item.quantity + 1)}
                            className="w-6 h-6 bg-gray-200 rounded hover:bg-gray-300"
                          >
                            +
                          </button>
                        </div>
                        <p className="font-bold text-green-600">
                          Bs. {(item.unit_price * item.quantity).toFixed(2)}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="border-t pt-4">
                  <div className="flex justify-between items-center text-lg font-bold">
                    <span>TOTAL:</span>
                    <span className="text-green-600">Bs. {getTotal().toFixed(2)}</span>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>

        <div className="p-6 border-t flex gap-2">
          <button
            onClick={handleSubmit}
            disabled={loading || cart.length === 0}
            className="btn-success flex-1"
          >
            {loading ? 'Añadiendo...' : `Añadir ${cart.length} Producto(s)`}
          </button>
          <button onClick={onClose} className="btn-secondary flex-1">
            Cancelar
          </button>
        </div>
      </div>
    </div>
  );
};

// Componente BookingCard
const BookingCard = ({ booking, onUpdate }) => {
  const [showPayment, setShowPayment] = useState(false);
  const [showPOS, setShowPOS] = useState(false);

  const handleCheckout = async () => {
    if (booking.balance > 0) {
      alert('Debe cobrar el saldo pendiente antes del check-out');
      return;
    }

    if (window.confirm('¿Realizar check-out?')) {
      try {
        await checkout(booking.id);
        alert('Check-out realizado exitosamente');
        onUpdate();
      } catch (error) {
        alert(error.response?.data?.error || 'Error al hacer check-out');
      }
    }
  };

  return (
    <div className="card">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="font-bold text-lg">{booking.full_name}</h3>
          <p className="text-sm text-gray-600">Habitación {booking.room_number}</p>
          <p className="text-xs text-gray-500">{booking.booking_code}</p>
        </div>
        <span className="badge badge-info capitalize">{booking.room_type}</span>
      </div>

      <div className="space-y-2 mb-4">
        <div className="flex justify-between text-sm">
          <span className="text-gray-600">Total:</span>
          <span className="font-semibold">Bs. {parseFloat(booking.total_amount).toFixed(2)}</span>
        </div>
        <div className="flex justify-between text-sm">
          <span className="text-gray-600">Pagado:</span>
          <span className="text-green-600">Bs. {parseFloat(booking.amount_paid).toFixed(2)}</span>
        </div>
        <div className="flex justify-between text-sm">
          <span className="text-gray-600">Saldo:</span>
          <span className={`font-bold ${booking.balance > 0 ? 'text-red-600' : 'text-green-600'}`}>
            Bs. {parseFloat(booking.balance).toFixed(2)}
          </span>
        </div>
      </div>

      <div className="flex gap-2">
        <button
          onClick={() => setShowPOS(true)}
          className="btn-secondary flex-1 text-sm"
        >
          <Plus className="w-4 h-4 inline mr-1" />
          Productos
        </button>
        {booking.balance > 0 && (
          <button onClick={() => setShowPayment(true)} className="btn-success flex-1 text-sm">
            Cobrar
          </button>
        )}
        <button onClick={handleCheckout} className="btn-primary flex-1 text-sm">
          Check-out
        </button>
      </div>

      {showPayment && (
        <PaymentModal
          booking={booking}
          onClose={() => setShowPayment(false)}
          onPaid={() => {
            setShowPayment(false);
            onUpdate();
          }}
        />
      )}

      {showPOS && (
        <POSModal
          booking={booking}
          onClose={() => setShowPOS(false)}
          onAdded={() => {
            setShowPOS(false);
            onUpdate();
          }}
        />
      )}
    </div>
  );
};

export default BookingCard;
