import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import {
  Home, DollarSign, LogOut, Menu, X, Search, Plus,
  CheckCircle, XCircle, ShoppingCart, User, Hotel
} from 'lucide-react';
import {
  openCashRegister, getCurrentCashRegister, closeCashRegister,
  searchBookings, createBooking, getAvailableRooms,
  searchCustomers, createCustomer, createPayment, checkout,
  getProducts, addCharges
} from '../services/api';

// Importar BookingCard desde su ubicación
import BookingCard from '../components/cashier/BookingCard';

// Componente Home del Cajero
const CashierHome = () => {
  const [cashRegister, setCashRegister] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadCashRegister();
  }, []);

  const loadCashRegister = async () => {
  try {
    const data = await getCurrentCashRegister();
    
    // Asegurar que todos los valores numéricos sean válidos
    const sanitizedData = {
      ...data,
      initial_cash: parseFloat(data.initial_cash || 0),
      total_cash_payments: parseFloat(data.total_cash_payments || 0),
      total_card_payments: parseFloat(data.total_card_payments || 0),
      total_transfer_payments: parseFloat(data.total_transfer_payments || 0),
      total_check_payments: parseFloat(data.total_check_payments || 0),
      expected_cash: parseFloat(data.expected_cash || 0),
      total_collected: parseFloat(data.total_collected || 0),
      total_transactions: parseInt(data.total_transactions || 0)
    };
    
    setCashRegister(sanitizedData);
  } catch (error) {
    console.error('Error al cargar caja:', error);
    setCashRegister(null);
  } finally {
    setLoading(false);
  }
};
  if (loading) {
    return <div className="flex justify-center items-center h-64">
      <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
    </div>;
  }

  return (
    <div className="space-y-6">
      {cashRegister ? (
        <>
          <h2 className="text-2xl font-bold text-gray-800">Tu Turno Actual</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="card bg-gradient-to-br from-green-500 to-green-600 text-white">
              <p className="text-green-100 text-sm">Total Efectivo</p>
              <p className="text-3xl font-bold mt-2">
                Bs. {parseFloat(cashRegister.total_cash_payments || 0).toFixed(2)}
              </p>
            </div>

            <div className="card bg-gradient-to-br from-blue-500 to-blue-600 text-white">
              <p className="text-blue-100 text-sm">Total Tarjeta/QR</p>
              <p className="text-3xl font-bold mt-2">
                Bs. {parseFloat(cashRegister.total_card_payments || 0).toFixed(2)}
              </p>
            </div>

            <div className="card bg-gradient-to-br from-purple-500 to-purple-600 text-white">
              <p className="text-purple-100 text-sm">Total Cobrado</p>
              <p className="text-3xl font-bold mt-2">
                Bs. {parseFloat(cashRegister.total_collected || 0).toFixed(2)}
              </p>
              <p className="text-purple-100 text-xs mt-1">
                {cashRegister.total_transactions || 0} transacciones
              </p>
            </div>
          </div>

          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Detalle de Caja</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span className="text-gray-600">Saldo Inicial:</span>
                <span className="font-semibold">Bs. {parseFloat(cashRegister.initial_cash).toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Efectivo Esperado:</span>
                <span className="font-semibold text-green-600">Bs. {parseFloat(cashRegister.expected_cash).toFixed(2)}</span>
              </div>
              <div className="flex justify-between text-sm text-gray-500">
                <span>Hora de apertura:</span>
                <span>{new Date(cashRegister.opening_time).toLocaleString('es-BO')}</span>
              </div>
            </div>
          </div>
        </>
      ) : (
        <OpenCashRegisterForm onOpened={loadCashRegister} />
      )}
    </div>
  );
};

// Formulario para abrir caja
const OpenCashRegisterForm = ({ onOpened }) => {
  const [initialCash, setInitialCash] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await openCashRegister(parseFloat(initialCash));
      alert('Caja abierta exitosamente');
      onOpened();
    } catch (error) {
      alert(error.response?.data?.error || 'Error al abrir caja');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto">
      <div className="card">
        <h2 className="text-2xl font-bold mb-6 text-center">Abrir Caja</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="label">Saldo Inicial en Efectivo (Bs.)</label>
            <input
              type="number"
              step="0.01"
              className="input"
              value={initialCash}
              onChange={(e) => setInitialCash(e.target.value)}
              placeholder="0.00"
              required
            />
          </div>
          <button
            type="submit"
            disabled={loading}
            className="btn-primary w-full"
          >
            {loading ? 'Abriendo...' : 'Abrir Caja'}
          </button>
        </form>
      </div>
    </div>
  );
};

// Componente de Gestión de Reservas
const BookingsManagement = () => {
  const [bookings, setBookings] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showNewBooking, setShowNewBooking] = useState(false);

  useEffect(() => {
    if (searchTerm.length >= 3) {
      loadBookings();
    }
  }, [searchTerm]);

  const loadBookings = async () => {
    try {
      const data = await searchBookings({ q: searchTerm, status: 'checked_in' });
      setBookings(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-800">Gestión de Reservas</h2>
        <button onClick={() => setShowNewBooking(true)} className="btn-primary">
          <Plus className="w-4 h-4 inline mr-2" />
          Nueva Reserva (Check-in)
        </button>
      </div>

      {/* Búsqueda */}
      <div className="card">
        <div className="relative">
          <Search className="absolute left-3 top-3 text-gray-400 w-5 h-5" />
          <input
            type="text"
            className="input pl-10"
            placeholder="Buscar por nombre, habitación o código..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {/* Lista de reservas */}
      {bookings.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {bookings.map((booking) => (
            <BookingCard key={booking.id} booking={booking} onUpdate={loadBookings} />
          ))}
        </div>
      )}

      {showNewBooking && (
        <NewBookingModal
          onClose={() => setShowNewBooking(false)}
          onCreated={() => {
            setShowNewBooking(false);
            loadBookings();
          }}
        />
      )}
    </div>
  );
};

// Modal de nueva reserva (simplificado)
// Modal de nueva reserva (simplificado)
// Modal de nueva reserva COMPLETO
const NewBookingModal = ({ onClose, onCreated }) => {
  const [step, setStep] = useState(1);
  const [customer, setCustomer] = useState(null);
  const [room, setRoom] = useState(null);
  const [customers, setCustomers] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);

  const [newCustomer, setNewCustomer] = useState({
    full_name: '',
    phone: '',
    document_number: ''
  });

  const [bookingData, setBookingData] = useState({
    check_in: new Date().toISOString().slice(0, 16),
    stay_type: 'daily',
    number_of_nights: 1,
    number_of_guests: 1
  });

  useEffect(() => {
    if (step === 2) loadRooms();
  }, [step]);

  const searchCustomersHandler = async () => {
    if (searchTerm.length < 3) return;
    try {
      const data = await searchCustomers(searchTerm);
      setCustomers(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const createCustomerHandler = async () => {
    if (!newCustomer.full_name) {
      alert('Ingrese el nombre del cliente');
      return;
    }
    setLoading(true);
    try {
      const result = await createCustomer(newCustomer);
      setCustomer({ ...newCustomer, id: result.customerId });
      setStep(2);
    } catch (error) {
      alert(error.response?.data?.error || 'Error al crear cliente');
    } finally {
      setLoading(false);
    }
  };

  const loadRooms = async () => {
    try {
      const data = await getAvailableRooms();
      setRooms(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const createBookingHandler = async () => {
    setLoading(true);
    try {
      await createBooking({
        customer_id: customer.id,
        room_id: room.id,
        ...bookingData
      });
      alert('Reserva creada exitosamente');
      onCreated();
    } catch (error) {
      alert(error.response?.data?.error || 'Error al crear reserva');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 overflow-y-auto">
      <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 my-8">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-2xl font-bold">Nueva Reserva - Paso {step} de 3</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* PASO 1: CLIENTE */}
        {step === 1 && (
          <div className="space-y-4">
            <h4 className="font-semibold">1. Seleccionar Cliente</h4>
            
            <div className="flex gap-2">
              <input
                type="text"
                className="input flex-1"
                placeholder="Buscar cliente..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button onClick={searchCustomersHandler} className="btn-primary">
                <Search className="w-5 h-5" />
              </button>
            </div>

            {customers.length > 0 && (
              <div className="border rounded-lg divide-y max-h-48 overflow-y-auto">
                {customers.map((c) => (
                  <div
                    key={c.id}
                    className="p-3 hover:bg-gray-50 cursor-pointer"
                    onClick={() => {
                      setCustomer(c);
                      setStep(2);
                    }}
                  >
                    <p className="font-semibold">{c.full_name}</p>
                    <p className="text-sm text-gray-600">{c.phone} - {c.document_number}</p>
                  </div>
                ))}
              </div>
            )}

            <div className="border-t pt-4">
              <h5 className="font-semibold mb-3">O crear nuevo cliente</h5>
              <div className="space-y-3">
                <input
                  type="text"
                  className="input"
                  placeholder="Nombre completo"
                  value={newCustomer.full_name}
                  onChange={(e) => setNewCustomer({...newCustomer, full_name: e.target.value})}
                />
                <input
                  type="text"
                  className="input"
                  placeholder="Teléfono (+59171234567)"
                  value={newCustomer.phone}
                  onChange={(e) => setNewCustomer({...newCustomer, phone: e.target.value})}
                />
                <input
                  type="text"
                  className="input"
                  placeholder="Documento"
                  value={newCustomer.document_number}
                  onChange={(e) => setNewCustomer({...newCustomer, document_number: e.target.value})}
                />
                <button
                  onClick={createCustomerHandler}
                  disabled={loading}
                  className="btn-primary w-full"
                >
                  {loading ? 'Creando...' : 'Crear y Continuar'}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* PASO 2: HABITACIÓN */}
        {step === 2 && (
          <div className="space-y-4">
            <div className="bg-blue-50 p-3 rounded-lg">
              <p className="text-sm"><strong>Cliente:</strong> {customer.full_name}</p>
            </div>

            <h4 className="font-semibold">2. Seleccionar Habitación</h4>

            <div className="grid grid-cols-2 md:grid-cols-3 gap-3 max-h-64 overflow-y-auto">
              {rooms.map((r) => (
                <div
                  key={r.id}
                  onClick={() => setRoom(r)}
                  className={`border-2 rounded-lg p-4 cursor-pointer ${
                    room?.id === r.id ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                  }`}
                >
                  <p className="font-bold text-lg">{r.room_number}</p>
                  <p className="text-sm text-gray-600 capitalize">{r.room_type}</p>
                  <p className="text-lg font-semibold text-green-600 mt-2">
                    Bs. {parseFloat(r.base_price).toFixed(2)}
                  </p>
                </div>
              ))}
            </div>

            <div className="flex gap-2">
              <button onClick={() => setStep(1)} className="btn-secondary flex-1">
                Atrás
              </button>
              <button
                onClick={() => setStep(3)}
                disabled={!room}
                className="btn-primary flex-1"
              >
                Continuar
              </button>
            </div>
          </div>
        )}

        {/* PASO 3: DETALLES */}
        {step === 3 && (
          <div className="space-y-4">
            <div className="bg-blue-50 p-3 rounded-lg space-y-1">
              <p className="text-sm"><strong>Cliente:</strong> {customer.full_name}</p>
              <p className="text-sm"><strong>Habitación:</strong> {room.room_number}</p>
            </div>

            <h4 className="font-semibold">3. Detalles de la Estadía</h4>

            <div className="space-y-3">
              <div>
                <label className="label">Tipo de Estadía</label>
                <select
                  className="input"
                  value={bookingData.stay_type}
                  onChange={(e) => setBookingData({...bookingData, stay_type: e.target.value})}
                >
                  <option value="daily">Por Noche</option>
                  <option value="3_hours">3 Horas</option>
                  <option value="6_hours">6 Horas</option>
                </select>
              </div>

              {bookingData.stay_type === 'daily' && (
                <div>
                  <label className="label">Número de Noches</label>
                  <input
                    type="number"
                    min="1"
                    className="input"
                    value={bookingData.number_of_nights}
                    onChange={(e) => setBookingData({...bookingData, number_of_nights: e.target.value})}
                  />
                </div>
              )}

              <div>
                <label className="label">Número de Huéspedes</label>
                <input
                  type="number"
                  min="1"
                  className="input"
                  value={bookingData.number_of_guests}
                  onChange={(e) => setBookingData({...bookingData, number_of_guests: e.target.value})}
                />
              </div>

              <div className="border-t pt-3">
                <div className="flex justify-between text-lg font-bold">
                  <span>Total:</span>
                  <span className="text-green-600">
                    Bs. {(parseFloat(room.base_price) * (bookingData.stay_type === 'daily' ? bookingData.number_of_nights : 1)).toFixed(2)}
                  </span>
                </div>
              </div>
            </div>

            <div className="flex gap-2">
              <button onClick={() => setStep(2)} className="btn-secondary flex-1">
                Atrás
              </button>
              <button
                onClick={createBookingHandler}
                disabled={loading}
                className="btn-success flex-1"
              >
                {loading ? 'Creando...' : 'Confirmar Check-in'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// Componente Principal del Cajero
const CashierDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const menuItems = [
    { path: '/cashier', icon: Home, label: 'Inicio' },
    { path: '/cashier/bookings', icon: ShoppingCart, label: 'Reservas' },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className={`bg-gray-900 text-white w-64 fixed inset-y-0 left-0 transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} md:translate-x-0 transition-transform duration-300 ease-in-out z-30`}>
        <div className="p-6 border-b border-gray-800">
          <div className="flex items-center gap-3">
            <DollarSign className="w-8 h-8 text-green-400" />
            <div>
              <h1 className="font-bold text-lg">Cajero</h1>
              <p className="text-xs text-gray-400">Sistema de Ventas</p>
            </div>
          </div>
        </div>

        <nav className="p-4 space-y-2">
          {menuItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-3 rounded-lg transition-colors ${
                location.pathname === item.path
                  ? 'bg-green-600 text-white'
                  : 'text-gray-300 hover:bg-gray-800'
              }`}
              onClick={() => setSidebarOpen(false)}
            >
              <item.icon className="w-5 h-5" />
              <span>{item.label}</span>
            </Link>
          ))}
        </nav>

        <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-gray-800">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-10 h-10 bg-green-600 rounded-full flex items-center justify-center">
              <User className="w-6 h-6" />
            </div>
            <div className="flex-1">
              <p className="text-sm font-medium">{user?.full_name}</p>
              <p className="text-xs text-gray-400">Cajero</p>
            </div>
          </div>
          <button
            onClick={handleLogout}
            className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-red-600 hover:bg-red-700 rounded-lg transition-colors"
          >
            <LogOut className="w-4 h-4" />
            Cerrar Sesión
          </button>
        </div>
      </aside>

      {/* Contenido principal */}
      <div className="flex-1 flex flex-col md:ml-64">
        {/* Header */}
        <header className="bg-white shadow-sm">
          <div className="flex items-center justify-between p-4">
            <button
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="md:hidden text-gray-600"
            >
              {sidebarOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
            </button>
            <div className="flex-1 md:flex-none">
              <h2 className="text-xl font-semibold text-gray-800">
                Panel de Cajero
              </h2>
            </div>
          </div>
        </header>

        {/* Contenido */}
        <main className="flex-1 overflow-y-auto p-6">
          <Routes>
            <Route path="/" element={<CashierHome />} />
            <Route path="/bookings" element={<BookingsManagement />} />
          </Routes>
        </main>
      </div>

      {/* Overlay para móvil */}
      {sidebarOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-20 md:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}
    </div>
  );
};

export default CashierDashboard;
