import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import {
  Home, DollarSign, LogOut, Menu, X, Search, User
} from 'lucide-react';
import {
  openCashRegister, getCurrentCashRegister, closeCashRegister,
  searchBookings, createPayment, checkout
} from '../services/api';

// ==================== COMPONENTE: HOME ====================
const CashierHome = () => {
  const [cashRegister, setCashRegister] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showCloseModal, setShowCloseModal] = useState(false);

  useEffect(() => {
    loadCashRegister();
  }, []);

  const loadCashRegister = async () => {
    try {
      const data = await getCurrentCashRegister();
      setCashRegister(data);
    } catch (error) {
      setCashRegister(null);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {cashRegister ? (
        <>
          <div className="flex justify-between items-center">
            <h2 className="text-2xl font-bold text-gray-800">Tu Turno Actual</h2>
            <button onClick={() => setShowCloseModal(true)} className="btn-danger">
              Cerrar Caja
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="card bg-gradient-to-br from-green-500 to-green-600 text-white">
              <p className="text-green-100 text-sm">Total Efectivo</p>
              <p className="text-3xl font-bold mt-2">
                Bs. {parseFloat(cashRegister.total_cash_payments || 0).toFixed(2)}
              </p>
            </div>
            <div className="card bg-gradient-to-br from-blue-500 to-blue-600 text-white">
              <p className="text-blue-100 text-sm">Total Tarjeta</p>
              <p className="text-3xl font-bold mt-2">
                Bs. {parseFloat(cashRegister.total_card_payments || 0).toFixed(2)}
              </p>
            </div>
            <div className="card bg-gradient-to-br from-purple-500 to-purple-600 text-white">
              <p className="text-purple-100 text-sm">Total Cobrado</p>
              <p className="text-3xl font-bold mt-2">
                Bs. {parseFloat(cashRegister.total_collected || 0).toFixed(2)}
              </p>
              <p className="text-purple-100 text-xs mt-1">
                {cashRegister.total_transactions || 0} transacciones
              </p>
            </div>
          </div>

          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Detalle de Caja</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span className="text-gray-600">Saldo Inicial:</span>
                <span className="font-semibold">Bs. {parseFloat(cashRegister.initial_cash).toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Efectivo Esperado:</span>
                <span className="font-semibold text-green-600">
                  Bs. {parseFloat(cashRegister.expected_cash).toFixed(2)}
                </span>
              </div>
            </div>
          </div>

          {showCloseModal && (
            <CloseCashModal
              cashRegister={cashRegister}
              onClose={() => setShowCloseModal(false)}
              onClosed={() => {
                setShowCloseModal(false);
                loadCashRegister();
              }}
            />
          )}
        </>
      ) : (
        <OpenCashForm onOpened={loadCashRegister} />
      )}
    </div>
  );
};

// ==================== COMPONENTE: ABRIR CAJA ====================
const OpenCashForm = ({ onOpened }) => {
  const [initialCash, setInitialCash] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await openCashRegister(parseFloat(initialCash));
      alert('Caja abierta exitosamente');
      onOpened();
    } catch (error) {
      alert(error.response?.data?.error || 'Error al abrir caja');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto">
      <div className="card">
        <h2 className="text-2xl font-bold mb-6 text-center">Abrir Caja</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="label">Saldo Inicial (Bs.)</label>
            <input
              type="number"
              step="0.01"
              className="input"
              value={initialCash}
              onChange={(e) => setInitialCash(e.target.value)}
              required
            />
          </div>
          <button type="submit" disabled={loading} className="btn-primary w-full">
            {loading ? 'Abriendo...' : 'Abrir Caja'}
          </button>
        </form>
      </div>
    </div>
  );
};

// ==================== COMPONENTE: CERRAR CAJA ====================
const CloseCashModal = ({ cashRegister, onClose, onClosed }) => {
  const [actualCash, setActualCash] = useState('');
  const [notes, setNotes] = useState('');
  const [loading, setLoading] = useState(false);

  const expectedCash = parseFloat(cashRegister.expected_cash || 0);
  const difference = actualCash ? parseFloat(actualCash) - expectedCash : 0;

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (window.confirm('¿Confirmar cierre de caja?')) {
      setLoading(true);
      try {
        await closeCashRegister(parseFloat(actualCash), notes);
        alert('Caja cerrada exitosamente');
        onClosed();
      } catch (error) {
        alert(error.response?.data?.error || 'Error');
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
        <h3 className="text-xl font-bold mb-4">Cerrar Caja</h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="label">Efectivo Real (Bs.)</label>
            <input
              type="number"
              step="0.01"
              className="input"
              value={actualCash}
              onChange={(e) => setActualCash(e.target.value)}
              required
            />
            {actualCash && (
              <p className="text-sm mt-2">
                Diferencia: <span className={difference === 0 ? 'text-green-600' : 'text-red-600'}>
                  Bs. {difference.toFixed(2)}
                </span>
              </p>
            )}
          </div>
          <div>
            <label className="label">Notas</label>
            <textarea className="input" rows="2" value={notes} onChange={(e) => setNotes(e.target.value)} />
          </div>
          <div className="flex gap-2">
            <button type="submit" disabled={loading} className="btn-danger flex-1">
              Cerrar
            </button>
            <button type="button" onClick={onClose} className="btn-secondary flex-1">
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// ==================== COMPONENTE: RESERVAS ====================
const BookingsManagement = () => {
  const [bookings, setBookings] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    if (searchTerm.length >= 3 || searchTerm.length === 0) {
      loadBookings();
    }
  }, [searchTerm]);

  const loadBookings = async () => {
    try {
      const data = await searchBookings({ q: searchTerm, status: 'checked_in' });
      setBookings(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Gestión de Reservas</h2>
      <div className="card">
        <div className="relative">
          <Search className="absolute left-3 top-3 text-gray-400 w-5 h-5" />
          <input
            type="text"
            className="input pl-10"
            placeholder="Buscar..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {bookings.map((b) => (
          <BookingCard key={b.id} booking={b} onUpdate={loadBookings} />
        ))}
      </div>
    </div>
  );
};

// ==================== COMPONENTE: CARD DE RESERVA ====================
const BookingCard = ({ booking, onUpdate }) => {
  const [showPayment, setShowPayment] = useState(false);

  const handleCheckout = async () => {
    if (booking.balance > 0) {
      alert('Debe cobrar el saldo pendiente');
      return;
    }
    if (window.confirm('¿Realizar check-out?')) {
      try {
        await checkout(booking.id);
        alert('Check-out exitoso');
        onUpdate();
      } catch (error) {
        alert('Error al hacer check-out');
      }
    }
  };

  return (
    <div className="card">
      <h3 className="font-bold text-lg">{booking.full_name}</h3>
      <p className="text-sm text-gray-600">Habitación {booking.room_number}</p>
      <div className="space-y-2 my-4">
        <div className="flex justify-between text-sm">
          <span>Total:</span>
          <span>Bs. {parseFloat(booking.total_amount).toFixed(2)}</span>
        </div>
        <div className="flex justify-between text-sm">
          <span>Saldo:</span>
          <span className={booking.balance > 0 ? 'text-red-600 font-bold' : 'text-green-600'}>
            Bs. {parseFloat(booking.balance).toFixed(2)}
          </span>
        </div>
      </div>
      <div className="flex gap-2">
        {booking.balance > 0 && (
          <button onClick={() => setShowPayment(true)} className="btn-success flex-1 text-sm">
            Cobrar
          </button>
        )}
        <button onClick={handleCheckout} className="btn-primary flex-1 text-sm">
          Check-out
        </button>
      </div>
      {showPayment && (
        <PaymentModal booking={booking} onClose={() => setShowPayment(false)} onPaid={() => { setShowPayment(false); onUpdate(); }} />
      )}
    </div>
  );
};

// ==================== COMPONENTE: MODAL DE PAGO ====================
const PaymentModal = ({ booking, onClose, onPaid }) => {
  const [amount, setAmount] = useState(booking.balance.toString());
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await createPayment({ booking_id: booking.id, amount: parseFloat(amount), payment_method: paymentMethod });
      alert('Pago registrado exitosamente');
      onPaid();
    } catch (error) {
      alert('Error al registrar pago');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
        <h3 className="text-xl font-bold mb-4">Registrar Pago</h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="label">Monto (Bs.)</label>
            <input type="number" step="0.01" className="input" value={amount} onChange={(e) => setAmount(e.target.value)} required />
          </div>
          <div>
            <label className="label">Método</label>
            <select className="input" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
              <option value="cash">Efectivo</option>
              <option value="card">Tarjeta</option>
              <option value="transfer">Transferencia</option>
            </select>
          </div>
          <div className="flex gap-2">
            <button type="submit" disabled={loading} className="btn-success flex-1">
              {loading ? 'Procesando...' : 'Registrar'}
            </button>
            <button type="button" onClick={onClose} className="btn-secondary flex-1">
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// ==================== DASHBOARD PRINCIPAL ====================
const CashierDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const menuItems = [
    { path: '/cashier', icon: Home, label: 'Inicio' },
    { path: '/cashier/bookings', icon: DollarSign, label: 'Reservas' },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      <aside className={`bg-gray-900 text-white w-64 fixed inset-y-0 left-0 transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} md:translate-x-0 transition-transform duration-300 z-30`}>
        <div className="p-6 border-b border-gray-800">
          <h1 className="font-bold text-lg">Cajero</h1>
        </div>
        <nav className="p-4 space-y-2">
          {menuItems.map((item) => (
            <Link key={item.path} to={item.path} className={`flex items-center gap-3 px-4 py-3 rounded-lg ${location.pathname === item.path ? 'bg-green-600' : 'hover:bg-gray-800'}`} onClick={() => setSidebarOpen(false)}>
              <item.icon className="w-5 h-5" />
              <span>{item.label}</span>
            </Link>
          ))}
        </nav>
        <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-gray-800">
          <p className="text-sm mb-2">{user?.full_name}</p>
          <button onClick={() => { logout(); navigate('/login'); }} className="w-full btn-danger">
            <LogOut className="w-4 h-4 inline mr-2" />
            Salir
          </button>
        </div>
      </aside>

      <div className="flex-1 md:ml-64">
        <header className="bg-white shadow-sm p-4">
          <button onClick={() => setSidebarOpen(!sidebarOpen)} className="md:hidden">
            {sidebarOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </header>
        <main className="p-6">
          <Routes>
            <Route path="/" element={<CashierHome />} />
            <Route path="/bookings" element={<BookingsManagement />} />
          </Routes>
        </main>
      </div>

      {sidebarOpen && <div className="fixed inset-0 bg-black bg-opacity-50 z-20 md:hidden" onClick={() => setSidebarOpen(false)} />}
    </div>
  );
};

export default CashierDashboard;
