import React, { useState } from 'react';
import { X, Plus, Trash2 } from 'lucide-react';
import { addCharges } from '../../services/api';

const AddChargesModal = ({ guest, onClose, onAdded }) => {
  const [charges, setCharges] = useState([
    { description: '', quantity: 1, unit_price: '' }
  ]);
  const [loading, setLoading] = useState(false);

  const addCharge = () => {
    setCharges([...charges, { description: '', quantity: 1, unit_price: '' }]);
  };

  const removeCharge = (index) => {
    setCharges(charges.filter((_, i) => i !== index));
  };

  const updateCharge = (index, field, value) => {
    const updated = [...charges];
    updated[index][field] = value;
    setCharges(updated);
  };

  const calculateTotal = () => {
    return charges.reduce((sum, charge) => {
      const price = parseFloat(charge.unit_price) || 0;
      const qty = parseInt(charge.quantity) || 0;
      return sum + (price * qty);
    }, 0);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const validCharges = charges.filter(c => c.description && c.unit_price);
    
    if (validCharges.length === 0) {
      alert('Agregue al menos un cargo v치lido');
      return;
    }

    setLoading(true);
    try {
      await addCharges(guest.id, validCharges);
      alert('Cargos agregados exitosamente');
      onAdded();
    } catch (error) {
      alert(error.response?.data?.error || 'Error al agregar cargos');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-xl font-bold">
            Agregar Cargos Extras - Hab. {guest.room_number}
          </h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="bg-blue-50 p-3 rounded-lg mb-4">
          <p className="font-semibold">{guest.full_name}</p>
          <p className="text-sm text-gray-600">
            Total actual: Bs. {parseFloat(guest.total_amount).toFixed(2)}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          {charges.map((charge, index) => (
            <div key={index} className="border rounded-lg p-4 bg-gray-50">
              <div className="flex justify-between items-center mb-3">
                <span className="font-semibold text-sm">Cargo #{index + 1}</span>
                {charges.length > 1 && (
                  <button
                    type="button"
                    onClick={() => removeCharge(index)}
                    className="text-red-500 hover:text-red-700"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                )}
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                <div className="md:col-span-1">
                  <label className="label">Descripci칩n</label>
                  <input
                    type="text"
                    className="input"
                    placeholder="Ej: Bebida, Comida, etc."
                    value={charge.description}
                    onChange={(e) => updateCharge(index, 'description', e.target.value)}
                    required
                  />
                </div>

                <div>
                  <label className="label">Cantidad</label>
                  <input
                    type="number"
                    className="input"
                    min="1"
                    value={charge.quantity}
                    onChange={(e) => updateCharge(index, 'quantity', e.target.value)}
                    required
                  />
                </div>

                <div>
                  <label className="label">Precio Unitario (Bs.)</label>
                  <input
                    type="number"
                    step="0.01"
                    className="input"
                    placeholder="0.00"
                    value={charge.unit_price}
                    onChange={(e) => updateCharge(index, 'unit_price', e.target.value)}
                    required
                  />
                </div>
              </div>

              {charge.quantity && charge.unit_price && (
                <p className="text-right text-sm mt-2 font-semibold">
                  Subtotal: Bs. {(parseFloat(charge.unit_price) * parseInt(charge.quantity)).toFixed(2)}
                </p>
              )}
            </div>
          ))}

          <button
            type="button"
            onClick={addCharge}
            className="btn-secondary w-full flex items-center justify-center gap-2"
          >
            <Plus className="w-4 h-4" />
            Agregar Otro Cargo
          </button>

          <div className="border-t pt-4">
            <div className="flex justify-between items-center mb-4">
              <span className="font-bold text-lg">Total de Cargos Extras:</span>
              <span className="font-bold text-2xl text-green-600">
                Bs. {calculateTotal().toFixed(2)}
              </span>
            </div>

            <div className="bg-yellow-50 border border-yellow-200 p-3 rounded-lg mb-4">
              <p className="text-sm text-yellow-800">
                El nuevo total de la cuenta ser치: <strong>Bs. {(parseFloat(guest.total_amount) + calculateTotal()).toFixed(2)}</strong>
              </p>
            </div>
          </div>

          <div className="flex gap-2">
            <button
              type="submit"
              disabled={loading}
              className="btn-success flex-1"
            >
              {loading ? 'Agregando...' : 'Confirmar Cargos'}
            </button>
            <button
              type="button"
              onClick={onClose}
              className="btn-secondary flex-1"
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddChargesModal;
